components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if theme_settings.productpage_bulk_price_table}}
        <div class="productView-bulkPricing">
            <p class="productView-bulkPricing-heading">{{lang 'products.bulk_pricing.modal_title'}}</p>
            <p class="productView-bulkPricing-description">{{lang 'products.bulk_pricing.instructions'}}</p>
            <table class="productView-bulkPricing-table table">
                <tbody class="table-tbody">
                    {{#each bulk_discount_rates}}
                    <tr>
                        <td>{{#if min '==' max}}{{lang 'products.bulk_pricing.range_single' min=min}}{{else}}{{lang 'products.bulk_pricing.range' min=min max=max}}{{/if}}</td>
                        <td>
                            {{#if type '===' 'percent'}}
                                {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                            {{else if type '===' 'fixed'}}
                                {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                            {{else if type '===' 'price'}}
                                {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                            {{/if}}
                        </td>
                    </tr>
                    {{/each}}
                </tbody>
            </table>
        </div>
    {{else}}
        {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
        <dd class="productView-info-value">
            <a href="{{product.url}}#bulk_pricing"
                {{#unless is_ajax}}
                    data-reveal-id="modal-bulk-pricing"
                {{else}}
                    {{#unless in_quickview}}
                        data-reveal-id="modal-bulk-pricing"
                    {{/unless}}
                {{/unless}}
            >
                {{lang 'products.bulk_pricing.view'}}
            </a>
        </dd>
        <div id="modal-bulk-pricing" class="modal modal--small" data-reveal>
            <div class="modal-header">
                <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
                <a href="#" class="modal-close" aria-label="Close"><svg class="icon"><use href="#icon-close" /></svg></a>
            </div>
            <div class="modal-body">
                <p>{{lang 'products.bulk_pricing.instructions'}}</p>
                <ul>
                    {{#each bulk_discount_rates}}
                    <li>
                        {{#if min '==' max}}{{lang 'products.bulk_pricing.range_single' min=min}}{{else}}{{lang 'products.bulk_pricing.range' min=min max=max}}{{/if}}
                        {{#if type '===' 'percent'}}
                        {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                        {{/if}}
                        {{#if type '===' 'fixed'}}
                        {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                        {{/if}}
                        {{#if type '===' 'price'}}
                        {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                        {{/if}}
                    </li>
                    {{/each}}
                </ul>
            </div>
        </div>
    {{/if}}
{{/if}}

components/products/list-item:
{{#if settings.data_tag_enabled}}
    <article class="listItem"
        data-event-type="{{event}}"
        data-entity-id="{{id}}"
        data-position="{{position}}"
        data-name="{{name}}"
        data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
        data-product-brand="{{brand.name}}"
        data-product-price="
            {{#or customer (unless settings.hide_price_from_guests)}}
                {{#if price.with_tax}}
                    {{price.with_tax.value}}
                {{else}}
                    {{price.without_tax.value}}
                {{/if}}
            {{else}}
                {{lang 'common.login_for_pricing'}}
            {{/or}}
        ">
{{else}}
    <article class="listItem" >
{{/if}}
    <figure class="listItem-figure">
        {{#if theme_settings.product_outstock_badge}}
            {{#or customer (unless settings.hide_price_from_guests)}}
                {{#if out_of_stock_message }}
                    <div class="sale-flag-side sale-flag-side--outstock">
                        <span class="sale-text">{{out_of_stock_message}}</span>
                    </div>
                {{/if}}
            {{/or}}
        {{/if}}
        {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
            {{#if theme_settings.product_sale_badges '===' 'sash'}}
                <div class="sale-flag-sash">
                    <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'topright'}}
                <div class="sale-flag-side">
                    <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'burst'}}
                <div class="starwrap">
                    <div class="sale-flag-star"></div>
                    <div class="sale-text-burst">{{lang 'emthemesmodez.on_sale'}}</div>
                </div>
            {{/if}}
        {{/or}}
        {{#if theme_settings.product_custom_badges}}
            {{#each custom_fields}}
