components/products/product-view:
{{assignVar 'productImageSizeAttrs' (get 'string' (concat 'width=' (join (split theme_settings.product_size 'x') ' height=')))}}
{{assignVar 'productThumbSizeAttrs' (get 'string' (concat 'width=' (join (split theme_settings.productthumb_size 'x') ' height=')))}}
<div class="productView productView--{{theme_settings.productpage_description_type}}"
    {{#if settings.data_tag_enabled}}
        data-event-type="product"
        data-entity-id="{{product.id}}"
        data-name="{{product.title}}"
        data-product-category="{{#each product.category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
        data-product-brand="{{product.brand.name}}"
        data-product-price="
            {{#or customer (unless settings.hide_price_from_guests)}}
                {{#if product.price.with_tax}}
                    {{product.price.with_tax.value}}
                {{else}}
                    {{product.price.without_tax.value}}
                {{/if}}
            {{else}}
                {{lang 'common.login_for_pricing'}}
            {{/or}}
        "
        data-product-variant="single-product-option"
    {{/if}}
>
    {{#each product.reviews.messages}}
        {{#if error}}
            {{>components/common/alert-error error}}
        {{/if}}
        {{#if success}}
            {{>components/common/alert-success success}}
        {{/if}}
    {{/each}}

    <div class="productView-detailsWrapper">
        <div data-also-bought-parent-scope class="productView-beforeAlsoBought">
            <section class="productView-details">
                <div class="productView-product">
                    {{#if product.brand}}
                    <h5 class="productView-brand">
                        <a href="{{product.brand.url}}"><span>{{product.brand.name}}</span></a>
                    </h5>
                    {{/if}}

                    <h1 class="productView-title">{{product.title}}</h1>

                    <div class="productView-rating">
                        {{#if settings.show_product_rating}}
                            {{> components/products/ratings rating=product.rating}}
                            <span class="productView-reviewLink">
                                {{#if product.num_reviews '>' 0}}
                                    <a href="{{product.url}}#{{#if theme_settings.show_product_reviews_tabs}}tab-reviews{{else}}product-reviews{{/if}}">
                                        {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                                    </a>
                                {{else}}
                                    {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                                {{/if}}
                            </span>
                        {{/if}}
                        {{#if settings.show_product_reviews}}
                            <span class="productView-reviewLink">
                                <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}"
                                    {{#unless is_ajax}}
                                        data-reveal-id="modal-review-form"
                                    {{else}}
                                        {{#unless in_quickview}}
                                            data-reveal-id="modal-review-form"
                                        {{/unless}}
                                    {{/unless}}
                                >
                                {{lang 'products.reviews.new'}}
                                </a>
                            </span>
                            {{> components/products/modals/writeReview}}
                        {{/if}}
                    </div>

                    {{#if theme_settings.bannerAfterRating '===' 'all'}}
                        <div data-emthemesmodez-remote-banner="emthemesModezBannersBlockAfterRating"></div>
                    {{/if}}

                    {{{region name="product_below_rating--global"}}}
                    {{{region name="product_below_rating"}}}

                    {{product.detail_messages}}
                    <dl class="productView-info">
                        <dt class="productView-info-name productView-info-name--sku sku-label"{{#unless product.sku}} style="display: none;"{{/unless}}>{{lang 'products.sku'}}</dt>
                        <dd class="productView-info-value productView-info-value--sku" data-product-sku>{{product.sku}}</dd>
                        <dt class="productView-info-name productView-info-name--upc upc-label"{{#unless product.upc}} style="display: none;"{{/unless}}>{{lang 'products.upc'}}</dt>
                        <dd class="productView-info-value productView-info-value--upc" data-product-upc>{{product.upc}}</dd>
                        <dt class="productView-info-name productView-info-name--mpn mpn-label" {{#unless product.mpn}} style="display: none;"{{/unless}}>{{lang 'products.mpn'}}</dt>
                        <dd class="productView-info-value productView-info-value--mpn" data-product-mpn data-original-mpn="{{product.mpn}}">{{product.mpn}}</dd>
                        {{#if product.condition}}
                            <dt class="productView-info-name productView-info-name--condition">{{lang 'products.condition'}}</dt>
                            <dd class="productView-info-value productView-info-value--condition">{{product.condition}}</dd>
                        {{/if}}
                        {{#if product.availability}}
                            <dt class="productView-info-name productView-info-name--availability">{{lang 'products.availability'}}</dt>
                            <dd class="productView-info-value productView-info-value--availability">{{product.availability}}</dd>
                        {{/if}}
                        {{#all product.weight theme_settings.show_product_weight}}
                            <dt class="productView-info-name productView-info-name--weight">{{lang 'products.weight'}}</dt>
                            <dd class="productView-info-value productView-info-value--weight" data-product-weight>{{product.weight}}</dd>
                        {{/all}}
                        {{#all product.width product.height product.depth theme_settings.show_product_dimensions}}
                            <dt class="productView-info-name productView-info-name--height">{{lang 'products.width'}}</dt>
                            <dd class="productView-info-value productView-info-value--height" data-product-width>
                                {{product.width}}
                                {{#if settings.measurements.length '==' 'Centimeters'}}
                                ({{lang 'products.measurement.metric'}})
                                {{else}}
                                ({{lang 'products.measurement.imperial'}})
                                {{/if}}
                            </dd>
                            <dt class="productView-info-name productView-info-name--height">{{lang 'products.height'}}</dt>
                            <dd class="productView-info-value productView-info-value--height" data-product-height>
                                {{product.height}}
                                {{#if settings.measurements.length '==' 'Centimeters'}}
                                ({{lang 'products.measurement.metric'}})
                                {{else}}
                                ({{lang 'products.measurement.imperial'}})
                                {{/if}}
                            </dd>
                            <dt class="productView-info-name productView-info-name--depth">{{lang 'products.depth'}}</dt>
                            <dd class="productView-info-value productView-info-value--depth" data-product-depth>
                                {{product.depth}}
                                {{#if settings.measurements.length '==' 'Centimeters'}}
                                ({{lang 'products.measurement.metric'}})
                                {{else}}
                                ({{lang 'products.measurement.imperial'}})
                                {{/if}}
                            </dd>
                        {{/all}}
                        {{#if product.min_purchase_quantity}}
                            <dt class="productView-info-name productView-info-name--minQty">{{lang 'products.min_purchase_quantity'}}</dt>
                            <dd class="productView-info-value productView-info-value--minQty">{{lang 'products.purchase_units' quantity=product.min_purchase_quantity}}</dd>
                        {{/if}}
                        {{#if product.max_purchase_quantity}}
                            <dt class="productView-info-name productView-info-name--maxQty">{{lang 'products.max_purchase_quantity'}}</dt>
                            <dd class="productView-info-value productView-info-value--maxQty">{{lang 'products.purchase_units' quantity=product.max_purchase_quantity}}</dd>
                        {{/if}}
                        {{#if product.gift_wrapping_available}}
                            <dt class="productView-info-name productView-info-name--giftWrap">{{lang 'products.gift_wrapping'}}</dt>
                            <dd class="productView-info-value productView-info-value--giftWrap">{{lang 'products.gift_wrapping_available'}}</dd>
                        {{/if}}
                        {{#if product.shipping}}
                            {{#if product.shipping.calculated}}
                                <dt class="productView-info-name productView-info-name--shippingCalc">{{lang 'products.shipping'}}</dt>
                                <dd class="productView-info-value productView-info-value--shippingCalc">{{lang 'products.shipping_calculated'}}</dd>
                            {{else}}
                                {{#if product.shipping.price.value '===' 0}}
                                    <dt class="productView-info-name productView-info-name--shippingFree">{{lang 'products.shipping'}}</dt>
                                    <dd class="productView-info-value productView-info-value--shippingFree">{{lang 'products.shipping_free'}}</dd>
                                {{else}}
                                    <dt class="productView-info-name productView-info-name--shippingFixed">{{lang 'products.shipping'}}</dt>
                                    <dd class="productView-info-value productView-info-value--shippingFixed">{{lang 'products.shipping_fixed' amount=product.shipping.price.formatted}}</dd>
                                {{/if}}
                            {{/if}}
                        {{/if}}
                        {{#unless theme_settings.productpage_bulk_price_table}}
                        {{#or customer (unless settings.hide_price_from_guests)}}
                        <div class="productView-info-bulkPricing">
                            {{> components/products/bulk-discount-rates bulk_discount_rates=product.bulk_discount_rates}}
                        </div>
                        {{/or}}
                        {{/unless}}

                        {{#unless theme_settings.productpage_custom_fields_tab}}
                            {{#if theme_settings.productpage_custom_fields_hide_special}}
                                {{#each product.custom_fields}}
                                    {{#startsWith '__' name}}
                                    {{else}}
                                        <dt class="productView-info-name productView-info-name--cf{{pascalcase name}}">{{name}}:</dt>
                                        <dd class="productView-info-value productView-info-value--cf{{pascalcase name}}">{{{value}}}</dd>
                                    {{/startsWith}}
                                {{/each}}
                            {{else}}
                                {{#each product.custom_fields}}
                                    <dt class="productView-info-name productView-info-name--cf{{pascalcase name}}">{{name}}:</dt>
                                    <dd class="productView-info-value productView-info-value--cf{{pascalcase name}}">{{{value}}}</dd>
                                {{/each}}
                            {{/if}}
                        {{/unless}}
                    </dl>

                    {{#if theme_settings.productpage_bulk_price_table}}
                    {{#or customer (unless settings.hide_price_from_guests)}}
                        <div class="productView-info-bulkPricing">
                            {{> components/products/bulk-discount-rates bulk_discount_rates=product.bulk_discount_rates}}
                        </div>
                    {{/or}}
                    {{/if}}
                </div>
            </section>

            <section class="productView-images {{#unless theme_settings.productpage_vertical_thumbnails}}productView-images--bottom{{/unless}}" data-image-gallery
                {{#if theme_settings.alsobought_position '!==' 'left'}}
                    data-sidebar-sticky
                {{/if}}
            >
                {{!--
                    Note that these image sizes are coupled to image sizes used in /assets/js/theme/common/product-details.js
                    for variant/rule image replacement
                --}}

                {{#if theme_settings.productpage_videos_position '===' 'images'}}
                    {{#if product.videos.list}}
                        {{assignVar 'videosInGallery' product.videos.list.length}}
                    {{else}}
                        {{assignVar 'videosInGallery' 0}}
                    {{/if}}
                {{else}}
                    {{assignVar 'videosInGallery' 0}}
                {{/if}}

                {{#or product.images (getVar 'videosInGallery')}}
                    <ul class="productView-imageCarousel-main" id="productView-imageCarousel-main-{{product.id}}" data-image-gallery-main>
                        {{#if theme_settings.product_image_responsive}}
                            {{#each product.images}}
                                <li class="productView-imageCarousel-main-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}">
                                    <div style="position: relative; display: inline-block; overflow: visible;">
                                        
                                        {{!-- Cinta lateral solo si el campo personalizado "etapa" es "Nuevo" --}}
                                        {{#each ../../custom_fields}}
                                            {{#if (and (eq name "etapa") (eq value "Nuevo"))}}
                                                <span style="
                                                    position: absolute;
                                                    top: 15px;
                                                    left: -55px;
                                                    background-color: #28a745;
                                                    color: white;
                                                    font-weight: bold;
                                                    font-size: 1rem;
                                                    padding: 8px 70px;
                                                    text-align: center;
                                                    transform: rotate(-45deg);
                                                    z-index: 1000;
                                                    box-shadow: 0 3px 8px rgba(0,0,0,0.25);
                                                ">
                                                    NUEVO
                                                </span>
                                            {{/if}}
                                        {{/each}}
                                        <a href="{{getImageSrcset this 'zoom_size' (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}&amp;imbypass=on"
                                            data-original-zoom="{{getImageSrcset this 'zoom_size' (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}&amp;imbypass=on"
                                            data-original-img="{{getImageSrcset this 'product_size' (cdn ../theme_settings.default_image_product) 1x=../theme_settings.product_size}}"
                                            data-original-srcset="{{getImageSrcset this use_default_sizes=true }}">
                                
                                            {{#startsWith this.data ../product.main_image.data}}
                                                {{!-- First image loads default quality image --}}
                                                {{#if ../../theme_settings.lazyload_mode '==' 'lazyload'}}
                                                    {{> components/common/responsive-img
                                                        image=this
                                                        fallback_size=../../../theme_settings.product_size
                                                        lazyload='disabled'
                                                        default_image=../../../theme_settings.default_image_product
                                                        lqip_size=../../../theme_settings.product_size
                                                        otherAttributes=(getVar 'productImageSizeAttrs')
                                                    }}
                                                {{else}}
                                                    {{> components/common/responsive-img
                                                        image=this
                                                        fallback_size=../../../theme_settings.product_size
                                                        lazyload=../../theme_settings.lazyload_mode
                                                        default_image=../../../theme_settings.default_image_product
                                                        lqip_size=../../../theme_settings.product_size
                                                        otherAttributes=(getVar 'productImageSizeAttrs')
                                                    }}
                                                {{/if}}
                                            {{else}}
                                                {{> components/common/responsive-img
                                                    image=this
                                                    fallback_size=../../theme_settings.product_size
                                                    lazyload=../../theme_settings.lazyload_mode
                                                    default_image=../../theme_settings.default_image_product
                                                    otherAttributes=(getVar 'productImageSizeAttrs')
                                                }}
                                            {{/startsWith}}
                                        </a>
                                    </div>
                                </li>

                            {{/each}}
                        {{else}}
                            {{#each product.images}} <li class="productView-imageCarousel-main-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}"> <a href="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on" data-original-zoom="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on" data-original-img="{{getImage this 'product_size' (cdn ../theme_settings.default_image_product)}}" data-original-srcset=""> {{#if @index '===' 0}} <img src="{{getImage this 'product_size' (cdn ../../theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}" {{getVar 'productImageSizeAttrs'}}> {{else}} <img data-lazy="{{getImage this 'product_size' (cdn ../../theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}" {{getVar 'productImageSizeAttrs'}}> {{/if}} </a> </li> {{/each}}


                        {{/if}}
                        {{#if theme_settings.productpage_videos_position '===' 'images'}}
                            {{#each product.videos.list}}
                                <li class="productView-imageCarousel-main-item _video">
                                    <a
                                        href="//i.ytimg.com/vi/{{id}}/hqdefault.jpg"
                                        data-image-gallery-item
                                        data-image-gallery-new-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                                        data-image-gallery-zoom-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                                        data-video-url="//www.youtube.com/embed/{{id}}?autoplay=1&mute=1&enablejsapi=1"
                                        data-video-id="{{id}}">
                                        <span class="productView-imageCarousel-main-item-img-container yt-video">
                                            <img src="//i.ytimg.com/vi/{{id}}/hqdefault.jpg" alt="{{title_long}}" title="{{title_long}}">
                                        </span>
                                    </a>
                                </li>
                            {{/each}}
                        {{/if}}
                    </ul>
                {{else}}
                    <ul class="productView-imageCarousel-main" id="productView-imageCarousel-main-{{product.id}}" data-image-gallery-main>
                        <li class="productView-imageCarousel-main-item slick-current">
                            {{#if theme_settings.lazyload_mode '==' 'lazyload'}}
                                {{!-- Disable lazyload for the first image if mode is not lazyload+lqip --}}
                                {{> components/common/responsive-img
                                    image=product.main_image
                                    fallback_size=theme_settings.product_size
                                    lazyload='disabled'
                                    default_image=theme_settings.default_image_product
                                    otherAttributes=(getVar 'productImageSizeAttrs')
                                }}
                            {{else}}
                                {{> components/common/responsive-img
                                    image=product.main_image
                                    fallback_size=theme_settings.product_size
                                    lazyload=theme_settings.lazyload_mode
                                    default_image=theme_settings.default_image_product
                                    otherAttributes=(getVar 'productImageSizeAttrs')
                                }}
                            {{/if}}
                        </li>
                    </ul>
                {{/or}}

                {{#if theme_settings.productpage_videos_position '===' 'images'}}
                    {{#if product.videos.list.length}}
                        <div class="productView-videoPlayer hide" data-video-player>
                            <div class="productView-videoPlayer-container">
                                <iframe
                                    data-video-player
                                    type="text/html"
                                    width="815"
                                    height="496"
                                    frameborder="0"
                                    webkitAllowFullScreen
                                    mozallowfullscreen
                                    allowFullScreen
                                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                    src="">
                                </iframe>
                            </div>
                        </div>
                    {{/if}}
                {{/if}}

                {{#or product.price.non_sale_price_with_tax product.price.non_sale_price_without_tax}}
                    {{#if theme_settings.product_sale_badges '===' 'sash'}}
                        <div class="sale-flag-sash">
                            <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
                        </div>
                    {{else if theme_settings.product_sale_badges '===' 'topright'}}
                        <div class="sale-flag-side">
                            <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
                        </div>
                    {{else if theme_settings.product_sale_badges '===' 'burst'}}
                        <div class="starwrap">
                            <div class="sale-flag-star"></div>
                            <div class="sale-text-burst">{{lang 'emthemesmodez.on_sale'}}</div>
                        </div>
                    {{/if}}
                {{/or}}

                {{#if theme_settings.product_custom_badges}}
                    {{#each product.custom_fields}}
                        {{#if name '===' '__badge'}}
                            <div class="sale-flag-side sale-flag-side--custom">
                                <span class="sale-text">{{{value}}}</span>
                            </div>
                        {{/if}}
                    {{/each}}
                {{/if}}


                {{#or (if product.images.length '>' 1) (getVar 'videosInGallery')}}
                    <ul class="productView-imageCarousel-nav" id="productView-imageCarousel-nav-{{product.id}}" data-image-gallery-nav data-image-gallery-nav-slides="{{theme_settings.productpage_thumbnails_count}}" {{#unless theme_settings.productpage_vertical_thumbnails}}data-image-gallery-nav-horizontal="true"{{/unless}}>
                        {{#each product.images}}
                            <li class="productView-imageCarousel-nav-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}">
                                {{> components/common/responsive-img
                                    image=this
                                    fallback_size=../theme_settings.productthumb_size
                                    lazyload=../theme_settings.lazyload_mode
                                    otherAttributes=(getVar 'productThumbSizeAttrs')
                                }}
                            </li>
                        {{/each}}

                        {{#if theme_settings.productpage_videos_position '===' 'images'}}
                            {{#each product.videos.list}}
                                <li class="productView-imageCarousel-nav-item _video">
                                    <button>
                                        <span class="productView-imageCarousel-nav-item-img-container yt-video">
                                            <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="//i.ytimg.com/vi/{{id}}/default.jpg" alt="{{title_long}}" title="{{title_long}}">
                                        </span>
                                    </button>
                                </li>
                            {{/each}}
                        {{/if}}
                    </ul>
                {{/or}}
            </section>


            <section class="productView-details">
                <div class="productView-options productView-options--{{theme_settings.product_options_column}}col">
                    {{#if product.release_date }}
                        <p>{{product.release_date}}</p>
                    {{/if}}

                    <div class="productView-price">
                        {{#if product.call_for_price}}
                            <span class="price--call">{{product.call_for_price}}</span>
                        {{else}}
                            {{#or customer (unless settings.hide_price_from_guests)}}
                                {{> components/products/price price=product.price}}
                            {{else}}
                                {{> components/common/login-for-pricing}}
                            {{/or}}
                        {{/if}}
                    </div>
                    {{{region name="product_below_price--global"}}}
                    {{{region name="product_below_price"}}}

                    <form class="form form--addToCart" method="post" action="{{product.cart_url}}" enctype="multipart/form-data"
                        data-cart-item-add>
                        <input type="hidden" name="action" value="add">
                        <input type="hidden" name="product_id" value="{{product.id}}"/>

                        <div class="emthemesModez-productView-optionsGrid">
                            {{#each product.customizations}}
                                {{{dynamicComponent 'components/products/customizations'}}}
                            {{/each}}
                            <div data-product-option-change style="display:none;">
                                {{#each product.options}}
                                    {{{dynamicComponent 'components/products/options'}}}
                                {{/each}}
                            </div>
                        </div>

                        <div class="form-field form-field--stock{{#unless product.stock_level}} u-hiddenVisually{{/unless}}">
                            <label class="form-label form-label--alternate">
                                {{lang 'products.current_stock'}}
                                <span data-product-stock>{{product.stock_level}}</span>
                            </label>
                        </div>

                        <div id="add-to-cart-wrapper{{#if in_quickview}}-{{product.id}}{{/if}}" class="add-to-cart-wrapper" {{#unless product.can_purchase}}style="display: none"{{/unless}}>
                            {{#if product.show_quantity_input}}
                                <div class="form-field form-field--increments _addToCartVisibility" {{#unless product.can_purchase}}style="display:none"{{/unless}}>
                                    <label class="form-label form-label--alternate"
                                        for="qty[]">{{lang 'products.quantity'}}</label>

                                    <div class="form-increment" data-quantity-change>
                                        <button class="button button--icon" data-action="dec">
                                            <span class="is-srOnly">{{lang 'products.quantity_decrease' name=product.title}}</span>
                                            <i class="icon" aria-hidden="true">
                                                <svg>
                                                    <use href="#icon-keyboard-arrow-down"/>
                                                </svg>
                                            </i>
                                        </button>
                                        <input class="form-input form-input--incrementTotal"
                                            id="qty[]"
                                            name="qty[]"
                                            type="tel"
                                            value="{{#if product.min_purchase_quantity}}{{product.min_purchase_quantity}}{{else}}1{{/if}}"
                                            data-quantity-min="{{product.min_purchase_quantity}}"
                                            data-quantity-max="{{product.max_purchase_quantity}}"
                                            min="1"
                                            pattern="[0-9]*"
                                            aria-live="polite">
                                        <button class="button button--icon" data-action="inc">
                                            <span class="is-srOnly">{{lang 'products.quantity_increase' name=product.title}}</span>
                                            <i class="icon" aria-hidden="true">
                                                <svg>
                                                    <use href="#icon-keyboard-arrow-up"/>
                                                </svg>
                                            </i>
                                        </button>
                                    </div>
                                </div>
                            {{/if}}
                            <div class="alertBox productAttributes-message" style="display:none">
                                <div class="alertBox-column alertBox-icon">
                                    <icon glyph="ic-success" class="icon" aria-hidden="true"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"></path></svg></icon>
                                </div>
                                <p class="alertBox-column alertBox-message"></p>
                            </div>
                            {{#or customer (unless settings.hide_price_from_guests)}}
                                <div class="form-action _addToCartVisibility" {{#unless product.can_purchase}}style="display:none"{{/unless}}>
                                    <input id="form-action-addToCart" data-wait-message="{{lang 'products.adding_to_cart'}}" class="button button--primary" type="submit"
                                        value="{{#if product.pre_order}}{{lang 'products.pre_order'}}{{else}}{{lang 'products.add_to_cart'}}{{/if}}">
                                    <div class="add-to-cart-wallet-buttons" data-add-to-cart-wallet-buttons>
                                        {{> components/common/wallet-buttons}}
                                    </div>
                                    <button id="form-action-addToCartLater" class="button button--primary" type="button" style="display:none">
                                        {{lang 'products.add_to_cart_later'}}
                                    </button>
                                </div>
                                {{{snippet 'product_addtocart'}}}
                            {{/or}}
                        </div>

                        {{#if product.out_of_stock}}
                            {{#if product.out_of_stock_message}}
                                {{>components/common/alert-error product.out_of_stock_message}}
                            {{else}}
                                {{>components/common/alert-error (lang 'products.sold_out')}}
                            {{/if}}
                        {{/if}}

                    </form>

                    {{#if settings.show_wishlist}}
                        {{> components/common/wishlist-dropdown}}
                    {{/if}}
                </div>

                {{#if theme_settings.bannerAfterAddToCartButton '===' 'all'}}
                    <div data-emthemesmodez-remote-banner="emthemesModezBannersBlockAfterAddToCartButton"></div>
                {{/if}}

                {{> components/common/share url=product.url}}
                {{{snippet 'product_details'}}}
            </section>
        </div>

        {{#if theme_settings.alsobought_position '===' 'left'}}
            {{> components/papa-supermarket/also-bought/also-bought}}
        {{else if theme_settings.alsobought_position '===' 'right'}}
            {{> components/papa-supermarket/also-bought/also-bought}}
        {{/if}}
    </div>

    {{#if theme_settings.alsobought_position '===' 'wide'}}
        {{> components/papa-supermarket/also-bought/also-bought}}
    {{/if}}

    {{#if theme_settings.bannerBeforeDescription '===' 'all'}}
        <div class="emthemesModezBannersBlockBeforeDescription-wrapper emthemesModezBannersBlockBeforeDescription-wrapper--{{theme_settings.productpage_description_type}}"><div data-emthemesmodez-remote-banner="emthemesModezBannersBlockBeforeDescription"></div></div>
    {{/if}}

    <div data-also-bought-parent-scope class="productView-descriptionWrapper">
        <article class="productView-description{{#if theme_settings.productpage_description_type '===' 'full'}} productView-description--full{{/if}}">
            <ul class="tabs tabs--alt" data-tab>
                <li class="tab tab--description is-active">
                    <a class="tab-title" href="#tab-description">{{lang 'products.description'}}</a>
                </li>
                {{#if theme_settings.productpage_videos_position '===' 'tab'}}
                    {{#if product.videos.list.length}}
                        <li class="tab tab--videos">
                            <a class="tab-title" href="#tab-videos">{{lang 'products.videos.header'}}</a>
                        </li>
                    {{/if}}
                {{/if}}
                {{#if product.warranty}}
                    <li class="tab tab--warranty">
                        <a class="tab-title" href="#tab-warranty">{{lang 'products.warranty'}}</a>
                    </li>
                {{/if}}

                {{#if (merge this specialCustomFields=0)}}{{/if}}
                {{#if theme_settings.productpage_custom_fields_hide_special}}
                    {{#each product.custom_fields}}
                        {{#startsWith '__' name}}
                            {{#if (merge ../../this specialCustomFields=(add ../../specialCustomFields 1))}}{{/if}}
                        {{/startsWith}}
                    {{/each}}
                {{/if}}

                {{#all product.custom_fields.length theme_settings.productpage_custom_fields_tab}}
                {{#any (unless theme_settings.productpage_custom_fields_hide_special) (if product.custom_fields.length '>' specialCustomFields )}}
                    <li class="tab tab--addition">
                        <a class="tab-title" href="#tab-addition">{{lang 'products.addition'}}</a>
                    </li>
                {{/any}}
                {{/all}}
                {{#all settings.show_product_reviews theme_settings.show_product_reviews_tabs}}
                <li class="tab tab--reviews">
                    <a class="tab-title productView-reviewTabLink" href="#tab-reviews">{{lang 'products.reviews.header' total=product.reviews.total}}</a>
                </li>
                {{/all}}
            </ul>
            <div class="tabs-contents">
                <div class="tab-content is-active" id="tab-description" data-emthemesmodez-mobile-collapse>
                    <h2 class="page-heading">{{lang 'products.description'}}</h2>
                    <div class="productView-description-tabContent emthemesModez-mobile-collapse-content" data-emthemesmodez-mobile-collapse-content {{#if settings.data_tag_enabled}} data-event-type="product" {{/if}}>
                        {{{product.description}}}
                        {{{snippet 'product_description'}}}
                    </div>
                    <a href="#" class="emthemesModez-mobile-collapse-handle" data-emthemesmodez-mobile-collapse-handle><span class="on">{{lang 'common.view_all'}}</span><span class="off">{{lang 'common.close'}}</span></a>
                </div>
                {{#if theme_settings.productpage_videos_position '===' 'tab'}}
                    {{#if product.videos.list.length}}
                        <div class="tab-content" id="tab-videos" data-emthemesmodez-mobile-collapse>
                            <h2 class="page-heading">{{lang 'products.videos.header'}}</h2>
                            <div class="productView-description-tabContent emthemesModez-mobile-collapse-content" data-emthemesmodez-mobile-collapse-content>
                                <section class="videoGallery{{#if theme_settings.productpage_videos_position '===' 'tab'}} videoGallery--inTab{{/if}}" data-video-gallery>
                                    <div class="videoGallery-main">
                                        <iframe
                                            id="player"
                                            type="text/html"
                                            width="640"
                                            height="390"
                                            frameborder="0"
                                            webkitAllowFullScreen
                                            mozallowfullscreen
                                            allowFullScreen
                                            src="https://www.youtube.com/embed/{{product.videos.featured.id}}?rel=0"
                                            loading="lazy"
                                            data-video-player>
                                        </iframe>
                                    </div>
                                    <ul class="videoGallery-list">
                                    {{#each product.videos.list}}
                                        <li class="videoGallery-item">
                                            <a href="#" class="video {{#if @first}}is-active{{/if}}" data-video-item data-video-id="{{id}}">
                                                <div class="video-figure">
                                                    <img class="lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="//i.ytimg.com/vi/{{id}}/default.jpg"/>
                                                </div>
                                                <div class="video-body">
                                                    <h5 class="video-title">{{title_long}}</h5>
                                                    <p class="video-description">{{description_short}}</p>
                                                </div>
                                            </a>
                                        </li>
                                    {{/each}}
                                    </ul>
                                </section>
                            </div>
                            <a href="#" class="emthemesModez-mobile-collapse-handle" data-emthemesmodez-mobile-collapse-handle><span class="on">{{lang 'common.view_all'}}</span><span class="off">{{lang 'common.close'}}</span></a>
                        </div>
                    {{/if}}
                {{/if}}
                {{#if product.warranty}}
                    <div class="tab-content" id="tab-warranty" data-emthemesmodez-mobile-collapse>
                        <h2 class="page-heading">{{lang 'products.warranty'}}</h2>
                        <div class="productView-description-tabContent emthemesModez-mobile-collapse-content" data-emthemesmodez-mobile-collapse-content>
                            {{{product.warranty}}}
                        </div>
                        <a href="#" class="emthemesModez-mobile-collapse-handle" data-emthemesmodez-mobile-collapse-handle><span class="on">{{lang 'common.view_all'}}</span><span class="off">{{lang 'common.close'}}</span></a>
                    </div>
                {{/if}}
                {{#all product.custom_fields.length theme_settings.productpage_custom_fields_tab}}
                {{#any (unless theme_settings.productpage_custom_fields_hide_special) (if product.custom_fields.length '>' specialCustomFields )}}
                    <div class="tab-content" id="tab-addition" data-emthemesmodez-mobile-collapse>
                        <h2 class="page-heading">{{lang 'products.addition'}}</h2>
                        <div class="productView-addition-tabContent emthemesModez-mobile-collapse-content" data-emthemesmodez-mobile-collapse-content>
                            <table class="productView-addition-table table">
                                <tbody class="table-tbody">
                                    {{#if theme_settings.productpage_custom_fields_hide_special}}
                                        {{#each product.custom_fields}}
                                            {{#startsWith '__' name}}
                                            {{else}}
                                                <tr class="productView-info-row--cf{{pascalcase name}}">
                                                    <td><div class="productView-info-name productView-info-name--cf{{pascalcase name}}">{{name}}:</div></td>
                                                    <td><div class="productView-info-value productView-info-value--cf{{pascalcase name}}">{{{value}}}</div></td>
                                                </tr>
                                            {{/startsWith}}
                                        {{/each}}
                                    {{else}}
                                        {{#each product.custom_fields}}
                                            <tr class="productView-info-row--cf{{pascalcase name}}">
                                                <td><div class="productView-info-name productView-info-name--cf{{pascalcase name}}">{{name}}:</div></td>
                                                <td><div class="productView-info-value productView-info-value--cf{{pascalcase name}}">{{{value}}}</div></td>
                                            </tr>
                                        {{/each}}
                                    {{/if}}
                                </tbody>
                            </table>
                        </div>
                        <a href="#" class="emthemesModez-mobile-collapse-handle" data-emthemesmodez-mobile-collapse-handle><span class="on">{{lang 'common.view_all'}}</span><span class="off">{{lang 'common.close'}}</span></a>
                    </div>
                {{/any}}
                {{/all}}
                {{#all settings.show_product_reviews theme_settings.show_product_reviews_tabs}}
                <div class="tab-content" id="tab-reviews" data-emthemesmodez-mobile-collapse>
                    <h2 class="page-heading">{{lang 'products.reviews.header' total=product.reviews.total}}</h2>
                    <div class="productView-description-tabContent emthemesModez-mobile-collapse-content" data-emthemesmodez-mobile-collapse-content>
                        {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
                    </div>
                    <a href="#" class="emthemesModez-mobile-collapse-handle" data-emthemesmodez-mobile-collapse-handle><span class="on">{{lang 'common.view_all'}}</span><span class="off">{{lang 'common.close'}}</span></a>
                </div>
                {{/all}}
            </div>
        </article>
    </div>

</div>

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/products/reviews:
{{#if reviews.total}}
<section class="productReviews" id="product-reviews" data-product-reviews>
    <ul class="productReviews-list">
        {{#each reviews.list}}
        <li class="productReview">
            <article>
                <header>
                    <span class="productReview-rating rating--small">
                        {{> components/products/ratings rating=rating}}
                        <span class="productReview-ratingNumber">{{ rating }}</span>
                    </span>
                    <p class="productReview-title">{{{sanitize title}}}</p>
                    {{#if name}}
                        <p class="productReview-author">
                            {{{lang 'products.reviews.post_on_by' date=date name=(sanitize name) }}}
                        </p>
                    {{else}}
                        <p class="productReview-author">
                            {{{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}}
                        </p>
                    {{/if}}
                </header>
                <p class="productReview-body">{{{sanitize text}}}</p>
            </article>
        </li>
        {{/each}}
    </ul>
    {{> components/common/paginator pagination.reviews instantload=true}}
</section>
{{/if}}
{{{snippet 'reviews'}}}

layout/instantload-base:
{{> components/papa-supermarket/common/global-vars}}
<div id='instantload-html-element'>
    <div id="instantload-head-element">
        <title>
            {{#if head.title}}
                {{ head.title }}
            {{else}}
                {{#if page_type '===' 'product'}}
                    {{#if product.page_title}}
                        {{product.page_title}}
                    {{else}}
                        {{product.title}} - {{settings.store_name}}
                    {{/if}}
                {{else if page_type '===' 'category'}}
                    {{category.name}} - {{settings.store_name}}
                {{else if page_type '===' 'brands'}}
                    {{settings.store_name}} {{lang 'brand.label'}}
                {{else if page_type '===' 'brand'}}
                    {{brand.name}} - {{settings.store_name}}
                {{else if page}}
                    {{page.title}} - {{settings.store_name}}
                {{else}}
                    {{settings.store_name}}
                {{/if}}
            {{/if}}
        </title>

        {{{ resourceHints }}}
        {{{ head.meta_tags }}}
        {{{ head.config }}}
        {{#block "head"}} {{/block}}

        {{! Include Captcha because BC doesn't include it from Ajax requests }}
        {{#if product.reviews.captcha}}
            <script src="https://www.google.com/recaptcha/api.js" async defer nonce="{{nonce}}"></script>
        {{/if}}

        {{{head.scripts}}}

        {{~inject 'themeSettings' theme_settings}}
        {{~inject 'genericError' (lang 'common.generic_error')}}
        {{~inject 'urls' urls}}
        {{~inject 'secureBaseUrl' settings.secure_base_url}}
        {{~inject 'cartId' cart_id}}
        {{~inject 'template' template}}
        {{~inject 'pageType' page_type}}
        {{~inject 'validationDictionaryJSON' (langJson 'validation_messages')}}
        {{~inject 'validationFallbackDictionaryJSON' (langJson 'validation_fallback_messages')}}
        {{~inject 'validationDefaultDictionaryJSON' (langJson 'validation_default_messages')}}
        {{~inject 'ajaxAddToCartEnterQty' (lang 'bulk_order.validate.enter_qty')}}
        {{~inject 'bulkOrderEnterQty' (lang 'bulk_order.validate.enter_qty')}}
        {{~inject 'bulkOrderChooseOptions' (lang 'bulk_order.validate.choose_options')}}
        {{~inject 'txtMinQty' (lang 'products.quantity_min' quantity='%qty%')}}
        {{~inject 'txtMaxQty' (lang 'products.quantity_max' quantity='%qty%')}}
        {{~inject 'compareAddonLang_compare' (lang 'common.compare')}}
        {{~inject 'compareAddonLang_quick_view' (lang 'products.quick_view')}}
        {{~inject 'compareAddonLang_remove' (lang 'compare.remove')}}
        {{~inject 'compareAddonLang_clear_all' (lang 'compare.clear_all')}}
        {{~inject 'categoryId' category.id}}
        {{#if theme_settings.navPages_autoFit}}
            {{~inject 'txtNavMore' (lang 'common.nav_more')}}
        {{/if}}
        {{~inject 'customerRecentlyViewedProductIds' (pluck customer.recently_viewed_products.items 'id')}}
        {{~inject 'customerId' customer.id}}
        {{~inject 'graphQLToken' settings.storefront_api.token}}
        {{~inject 'currencyCode' currency_selector.active_currency_code}}
        {{~inject 'hidePriceFromGuests' settings.hide_price_from_guests}}
        {{~inject 'showProductRating' settings.show_product_rating}}
        {{~inject 'defaultProductImage' (cdn theme_settings.default_image_product)}}
        {{~inject 'card_show_sku' theme_settings.show_sku}}
        {{~inject 'card_show_hover_image' theme_settings.card_showHoverImage}}
        {{~inject 'show_product_quick_view' theme_settings.show_product_quick_view}}
        {{~inject 'ajax_add_to_cart' theme_settings.ajax_add_to_cart}}
        {{~inject 'product_outstock_badge' theme_settings.product_outstock_badge}}
        {{~inject 'product_sale_badges' theme_settings.product_sale_badges}}
        {{~inject 'product_sale_label' (lang 'emthemesmodez.on_sale')}}
        {{~inject 'product_custom_badges' theme_settings.product_custom_badges}}
        {{~inject 'pdp_price_label' theme_settings.pdp-price-label}}
        {{~inject 'pdp_sale_price_label' theme_settings.pdp-sale-price-label}}
        {{~inject 'pdp_non_sale_price_label' theme_settings.pdp-non-sale-price-label}}
        {{~inject 'pdp_retail_price_label' theme_settings.pdp-retail-price-label}}
        {{~inject 'price_ranges' theme_settings.price_ranges}}
        {{~inject 'pbcst_show_bestselling' theme_settings.productsByCategorySortingTabs_show_bestselling}}
        {{~inject 'pbcst_show_featured' theme_settings.productsByCategorySortingTabs_show_featured}}
        {{~inject 'pbcst_show_new' theme_settings.productsByCategorySortingTabs_show_new}}
        {{~inject 'pbcst_show_reviews' theme_settings.productsByCategorySortingTabs_show_reviews}}
        {{~inject 'pbcst_show_banner' theme_settings.productsByCategorySortingTabs_showBanner}}
        {{~inject 'pbcst_subcategories' theme_settings.productsByCategorySortingTabs_subcategories}}
        {{~inject 'pbcst_products' theme_settings.productsByCategorySortingTabs_products}}
        {{~inject 'loadingImage' (cdn 'img/loading.svg')}}
        {{~inject 'productLoadingImage' (cdn theme_settings.image_product_loading)}}
        {{~inject 'txtPriceWithTax' (lang 'products.price_with_tax' tax_label=settings.tax_label)}}
        {{~inject 'txtPriceWithoutTax' (lang 'products.price_without_tax' tax_label=settings.tax_label)}}
        {{~inject 'txtQuickView' (lang 'products.quick_view')}}
        {{~inject 'txtCompare' (lang 'products.compare')}}
        {{~inject 'txtChooseOptions' (lang 'products.choose_options')}}
        {{~inject 'txtPreOrder' (lang 'products.pre_order')}}
        {{~inject 'txtAddToCart' (lang 'products.add_to_cart')}}
        {{~inject 'txtQuantity' (lang 'products.quantity')}}
        {{~inject 'txtQuantityDecrease' (lang 'products.quantity_decrease')}}
        {{~inject 'txtQuantityIncrease' (lang 'products.quantity_increase')}}
        {{~inject 'txtBestsellingTab' (lang 'papasupermarket.products_by_category_sorting_tabs.bestselling')}}
        {{~inject 'txtFeaturedTab' (lang 'papasupermarket.products_by_category_sorting_tabs.featured')}}
        {{~inject 'txtNewTab' (lang 'papasupermarket.products_by_category_sorting_tabs.new')}}
        {{~inject 'txtReviewsTab' (lang 'papasupermarket.products_by_category_sorting_tabs.reviews')}}
        {{~inject 'txtSubcategoryHeading' (lang 'papasupermarket.products_by_category_sorting_tabs.subcategory_title')}}
        {{~inject 'txtLoadMore' (lang 'papasupermarket.products_by_category_sorting_tabs.load_more')}}
        {{~inject 'txtCollapse' (lang 'papasupermarket.products_by_category_sorting_tabs.collapse')}}
        {{~inject 'outOfStockDefaultMessage' (lang 'products.out_of_stock_default_message')}}
        {{{snippet 'htmlhead'}}}
    </div>
    <div id="instantload-body-element" class="csscolumns papaSupermarket-layout--{{theme_settings.layout}}
        papaSupermarket-style--{{theme_settings.style}}
        papaSupermarket-pageType--{{dashcase page_type}}
        papaSupermarket-page--{{dashcase template}}
        {{#if theme_settings.product_uniform}}emthemesModez-productUniform{{/if}}
        {{#if theme_settings.hide_content_navigation}}papaSupermarket--hideContentNav{{/if}}
        {{#if theme_settings.show_allcategories_menu}}papaSupermarket--showAllCategoriesMenu{{/if}}
        {{#if theme_settings.show_subcategories_menu}}papaSupermarket--showSubcategoriesMenu{{/if}}
        {{#if product_results.faceted_search_enabled}}has-faceted{{/if}}
        {{#if category.faceted_search_enabled}}has-faceted{{/if}}
        {{#if brands.faceted_search_enabled}}has-faceted{{/if}}
        {{#if brand.faceted_search_enabled}}has-faceted{{/if}}
        {{#if result_count '>' 0}}has-result{{/if}}
        " id="topOfPage">

        {{#if banners.top_metadata}}
        <div class="banners" data-banner-location="top" id="instantload-banners-top">
            {{#if settings.data_tag_enabled}}
                {{#each banners.top_metadata}}
                    {{#if (occurrences this.content 'emthemesModezBannersBlock') '===' 0}}
                        <div class="banner">
                            {{{this.content}}}
                        </div>
                    {{/if}}
                {{/each}}
            {{else}}
                {{#each banners.top_metadata}}
                    {{#if (occurrences this.content 'emthemesModezBannersBlock') '===' 0}}
                        <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-banner-id="{{this.banner-name}}">
                            <div data-event-type="promotion-click">
                                {{{this.content}}}
                            </div>
                        </div>
                    {{/if}}
                {{/each}}
            {{/if}}
        </div>
        {{/if}}

        <div class="body" data-currency-code="{{currency_selector.active_currency_code}}" id="instantload-page-body">
            {{#block "hero"}} {{/block}}
            <div class="container">
                {{#block "page"}} {{/block}}
            </div>
            {{> components/common/modal}}
            {{> components/common/alert-modal}}
        </div>


        {{#if banners.bottom}}
            {{#if settings.data_tag_enabled}}
                <div class="banners banners--bottom" data-banner-location="bottom" id="instantload-banners-bottom">
                    {{#each (limit banners.bottom_metadata 1)}}
                        <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                            <div data-event-type="promotion-click">
                                {{{this.content}}}
                            </div>
                        </div>
                    {{/each}}
                </div>
            {{else}}
                <div class="banners banners--bottom" data-banner-location="bottom" id="instantload-banners-bottom">
                    <div class="banner">
                        {{{limit banners.bottom 1}}}
                    </div>
                </div>
            {{/if}}
        {{/if}}

        <script id="instantload-script" nonce="{{nonce}}">
            // Exported in app.js
            (function() {
                var parentContext = window.themeInstantLoad_jsContext || {};
                var context = JSON.parse({{jsContext}} || '{}');
                Object.keys(context).forEach(function(key) {
                    if (context[key] === null && parentContext[key] !== undefined) {
                        context[key] = parentContext[key];
                    }
                });
                window.stencilBootstrap("{{page_type}}", JSON.stringify(context), false).load();
            })();
        </script>

        {{{footer.scripts}}}
        {{{snippet 'footer'}}}
    </div>
</div>

